fetch("http://localhost:3000/api/teddies").then((e=>e.json())).then((e=>{for(const t of e){const e=document.getElementById("items"),n=document.createElement("a");n.setAttribute("href",`item.html?index=${t._id}`),e.appendChild(n);const c=document.createElement("article");n.appendChild(c);const d=document.createElement("img");d.setAttribute("src",t.imageUrl),d.setAttribute("alt",`Peluche ${t.name}`),c.appendChild(d);const i=document.createElement("h2");i.textContent=t.name,c.appendChild(i);const l=document.createElement("p");l.textContent=t.price/100+"€",c.appendChild(l)}})).catch((e=>document.getElementById("items").innerHTML="<p>Désolé, aucun article n'est disponible.</p>"));